            perror("pthread_create");
            student_count = i;
            break;
        }
        usleep(50000); // stagger arrivals slightly
    }

    for (int i = 0; i < student_count; ++i) {
        if (threads[i] != 0) {
            pthread_join(threads[i], NULL);
        }
    }

    free(threads);
    free(ids);

    return EXIT_SUCCESS;
}
